import React from 'react';
import { InlineIcon } from '..';

export type FormStatusProps = {
	type: 'warning' | 'error' | 'success';
	id: string;
	// Unique id should be used with aria-describedby: https://www.deque.com/blog/anatomy-of-accessible-forms-error-messages/
	message?: string;
};

export const FormStatus = ({ message, type, id }: FormStatusProps) => {
	return (
		<span className={`usa-${type}-message`} id={id} role={type === 'error' ? 'alert' : 'status'}>
			{type === 'warning' && message ? <InlineIcon icon="incomplete" /> : ''} {message}
		</span>
	);
};
try {
    // @ts-ignore
    FormStatus.displayName = "FormStatus";
    // @ts-ignore
    FormStatus.__docgenInfo = { "description": "", "displayName": "FormStatus", "props": { "type": { "defaultValue": null, "description": "", "name": "type", "required": true, "type": { "name": "\"warning\" | \"error\" | \"success\"" } }, "id": { "defaultValue": null, "description": "", "name": "id", "required": true, "type": { "name": "string" } }, "message": { "defaultValue": null, "description": "", "name": "message", "required": false, "type": { "name": "string" } } } };
    // @ts-ignore
    if (typeof STORYBOOK_REACT_CLASSES !== "undefined")
