        STORYBOOK_REACT_CLASSES["src/components/FormStatus/FormStatus.tsx#FormStatus"] = { docgenInfo: FormStatus.__docgenInfo, name: "FormStatus", path: "src/components/FormStatus/FormStatus.tsx#FormStatus" };
}
catch (__react_docgen_typescript_loader_error) { }
