import React, { useContext, isValidElement, ReactEventHandler, Children } from 'react';
import { CardContext } from '..';

/**
 * Component for declaratively expanding/collapsing card
 * @param props
 */
export const ExpandCard: React.FC = ({ children }) => {
	const { toggleExpanded } = useContext(CardContext);

	if (!isValidElement(children)) {
		throw new Error('Invalid children to ExpandCard');
	}

	return (
		<>
			{Children.map(children, (child) => {
				const { type: Type, props, key } = child;
				return (
					<Type
						{...key}
						{...props}
						onClick={(e: ReactEventHandler) => {
							toggleExpanded();
							if (props.onClick) {
								props.onClick(e);
							}
						}}
					/>
				);
			})}
		</>
	);
};
try {
    // @ts-ignore
    ExpandCard.displayName = "ExpandCard";
    // @ts-ignore
    ExpandCard.__docgenInfo = { "description": "Component for declaratively expanding/collapsing card\n@param props", "displayName": "ExpandCard", "props": {} };
    // @ts-ignore
    if (typeof STORYBOOK_REACT_CLASSES !== "undefined")
        // @ts-ignore
        STORYBOOK_REACT_CLASSES["src/components/Card/ExpandCard.tsx#ExpandCard"] = { docgenInfo: ExpandCard.__docgenInfo, name: "ExpandCard", path: "src/components/Card/ExpandCard.tsx#ExpandCard" };
}
catch (__react_docgen_typescript_loader_error) { }
