import React from 'react';
import cx from 'classnames';
import { InlineIcon, Icon, Button } from '..';

export type SideNavItemProps = {
	title: string | JSX.Element;
	description: string;
	icon?: Icon;
	onClick?: () => any;
	content: JSX.Element;
};

type InternalSideNavItemProps = SideNavItemProps & {
	active: boolean;
	onClick: () => any;
};

export const SideNavItem = ({
	title,
	description,
	active,
	onClick,
	icon,
}: InternalSideNavItemProps) => {
	return (
		<li className={cx('oec-sidenav__item', { active })}>
			<Button
				onClick={onClick}
				appearance="unstyled"
				text={
					<div>
						<p className="oec-sidenav-item__title">
							{title} {icon && <InlineIcon icon={icon} />}
						</p>
						<p className="oec-sidenav-item__desc">{description}</p>
					</div>
				}
			></Button>
		</li>
	);
};
try {
    // @ts-ignore
    SideNavItem.displayName = "SideNavItem";
    // @ts-ignore
    SideNavItem.__docgenInfo = { "description": "", "displayName": "SideNavItem", "props": { "title": { "defaultValue": null, "description": "", "name": "title", "required": true, "type": { "name": "string | Element" } }, "description": { "defaultValue": null, "description": "", "name": "description", "required": true, "type": { "name": "string" } }, "icon": { "defaultValue": null, "description": "", "name": "icon", "required": false, "type": { "name": "any" } }, "onClick": { "defaultValue": null, "description": "", "name": "onClick", "required": false, "type": { "name": "(() => any) | (() => any)" } }, "content": { "defaultValue": null, "description": "", "name": "content", "required": true, "type": { "name": "Element" } }, "active": { "defaultValue": null, "description": "", "name": "active", "required": true, "type": { "name": "boolean" } } } };
    // @ts-ignore
    if (typeof STORYBOOK_REACT_CLASSES !== "undefined")
        // @ts-ignore
        STORYBOOK_REACT_CLASSES["src/components/SideNav/SideNavItem.tsx#SideNavItem"] = { docgenInfo: SideNavItem.__docgenInfo, name: "SideNavItem", path: "src/components/SideNav/SideNavItem.tsx#SideNavItem" };
}
catch (__react_docgen_typescript_loader_error) { }
